use bevy::prelude::*; use bevy_rapier2d::prelude::*; use crate::core::config::GameConfig; pub struct PhysicsSetupPlugin; impl Plugin for PhysicsSetupPlugin { fn build(&self, app: &mut App) { app.add_plugins((RapierPhysicsPlugin::<NoUserData>::default(),)).add_systems(Startup, configure_gravity); } }
fn configure_gravity(mut q_cfg: Query<&mut RapierConfiguration>, _game_cfg: Res<GameConfig>) { if let Ok(mut cfg) = q_cfg.single_mut() { cfg.gravity = Vect::new(0.0, 0.0); } }
